method UnquoteString(s: string, quoteChar: char) returns (result: string)
{
  var i := 1;
  result := "";

  while i < |s|-1
  {
    if s[i] == '\\' && i+1 < |s|-1 && s[i+1] == quoteChar {

      i := i + 2;
    } else {
      if s[i] != quoteChar {  
        result := result + [s[i]];
      }
      i := i + 1;
    }
  }
}
method ParseNumber(s: string) returns (result: real)
{


  result := 0.0;
  var i := 0;
  while i < |s|
  {
    if s[i] != '.' {
      result := result * 10.0 + (var digit := s[i] as int - '0' as int; digit as real);
    }
    i := i + 1;
  }
}

