method UnquoteString(s: string, quoteChar: char) returns (result: string)
  requires |s| >= 2 
  requires s[0] == quoteChar && s[|s|-1] == quoteChar
  ensures |result| <= |s| 
  ensures forall i :: 0 <= i < |result| ==> result[i] != quoteChar 
{
  var i := 1;
  result := "";

  while i < |s|-1
    invariant 1 <= i <= |s|-1
    invariant |result| <= i-1 
    invariant forall k :: 0 <= k < |result| ==> result[k] != quoteChar
    decreases |s| - i
  {
    if s[i] == '\\' && i+1 < |s|-1 && s[i+1] == quoteChar {

      i := i + 2;
    } else {
      if s[i] != quoteChar {  
        result := result + [s[i]];
      }
      i := i + 1;
    }
  }
}
method ParseNumber(s: string) returns (result: real)
  requires forall i :: 0 <= i < |s| ==> ('0' <= s[i] <= '9' || s[i] == '.')
  ensures result >= 0.0
{


  result := 0.0;
  var i := 0;
  while i < |s|
    invariant 0 <= i <= |s|
    invariant result >= 0.0
    decreases |s| - i
  {
    if s[i] != '.' {
      result := result * 10.0 + (var digit := s[i] as int - '0' as int; digit as real);
    }
    i := i + 1;
  }
}

