module RequirementsProcessor {

  datatype Line = Line(content: string)


  type RequirementsList = seq<string>


  method StripComments(lines: seq<Line>) returns (result: RequirementsList)
  {
    result := [];

    var i := 0;
    while i < |lines|
    {
      var line := lines[i].content;

      var trimmed := Trim(line);

      if trimmed != "" && !StartsWithChar(trimmed, '#') {
        var processedLine := trimmed;


        if ContainsChar(trimmed, '#') {
          var commentIndex := IndexOf(trimmed, '#');
          processedLine := trimmed[..commentIndex];
          processedLine := Trim(processedLine);
        }

        if processedLine != "" {
          result := result + [processedLine];
        }
      }

      i := i + 1;
    }
  }


  method GetRequirements(filename: string) returns (requirements: RequirementsList)
  {


    var fileLines := ReadFile(filename);
    requirements := StripComments(fileLines);
  }


  method ReadFile(filename: string) returns (lines: seq<Line>)
  {


    if filename == "requirements.txt" {
      lines := [
        Line("# This is a comment"),
        Line("package1>=1.0.0"),
        Line("package2==2.0.0 # With a comment"),
        Line(""),
        Line("  package3  ")
      ];
    } else if filename == "test-requirements.txt" {
      lines := [
        Line("pytest>=3.0.0"),
        Line("# Test packages"),
        Line("nose # Testing tool")
      ];
    } else {
      lines := [];
    }
  }





  function Trim(s: string): string
  {
    s 
  }


  predicate StartsWithChar(s: string, c: char)
  {
    s[0] == c
  }


  predicate ContainsChar(s: string, c: char)
  {
    exists i :: 0 <= i < |s| && s[i] == c
  }


  function IndexOf(s: string, c: char): int
  {
    if |s| == 0 then -1
    else if s[0] == c then 0
    else if IndexOf(s[1..], c) == -1 then -1
    else 1 + IndexOf(s[1..], c)
  }


  method Main()
  {
    var reqFile := "requirements.txt";
    var testReqFile := "test-requirements.txt";

    var requirements := GetRequirements(reqFile);
    var testRequirements := GetRequirements(testReqFile);



  }
}

