type Polygon = seq<Point>
class GeometryHelpers {

    method CreateRectangle(leftY: real, rightY: real, bottomZ: real, topZ: real) returns (rect: Rectangle)
        requires leftY <= rightY  
        requires bottomZ <= topZ  
        ensures rect.leftY == leftY && rect.rightY == rightY 
        ensures rect.bottomZ == bottomZ && rect.topZ == topZ
    {
        rect := Rectangle(leftY, rightY, bottomZ, topZ);
    }

    method GetPolygonCorner(polygon: Polygon) returns (corners: Rectangle)
        requires |polygon| >= 4  
        ensures corners.leftY <= corners.rightY
        ensures corners.bottomZ <= corners.topZ
    {
        var minY := polygon[0].y;
        var maxY := polygon[0].y;
        var minZ := polygon[0].z;
        var maxZ := polygon[0].z;

        var i := 1;
        while i < |polygon|
            invariant 1 <= i <= |polygon|
            invariant minY <= maxY
            invariant minZ <= maxZ
            invariant forall k :: 0 <= k < i ==> minY <= polygon[k].y <= maxY
            invariant forall k :: 0 <= k < i ==> minZ <= polygon[k].z <= maxZ
        {
            if polygon[i].y < minY { minY := polygon[i].y; }
            if polygon[i].y > maxY { maxY := polygon[i].y; }
            if polygon[i].z < minZ { minZ := polygon[i].z; }
            if polygon[i].z > maxZ { maxZ := polygon[i].z; }
            i := i + 1;
        }
        corners := Rectangle(minY, maxY, minZ, maxZ);
    }

    method MeasurePolygonsWidthAlongYAxis(polygons: seq<Polygon>) returns (intervals: seq<(real, real)>)
        requires forall i :: 0 <= i < |polygons| ==> |polygons[i]| > 0
        ensures |intervals| == |polygons|
        ensures forall i :: 0 <= i < |intervals| ==> intervals[i].0 <= intervals[i].1
    {
        var result: seq<(real, real)> := [];

        var i := 0;
        while i < |polygons|
            invariant 0 <= i <= |polygons|
            invariant |result| == i
            invariant forall k :: 0 <= k < |result| ==> result[k].0 <= result[k].1
        {
            var minY := polygons[i][0].y;
            var maxY := polygons[i][0].y;

            var j := 1;
            while j < |polygons[i]|
                invariant 1 <= j <= |polygons[i]|
                invariant minY <= maxY
            {
                if polygons[i][j].y < minY { minY := polygons[i][j].y; }
                if polygons[i][j].y > maxY { maxY := polygons[i][j].y; }
                j := j + 1;
            }

            result := result + [(minY, maxY)];
            i := i + 1;
        }

        intervals := result;
    }
}

