  ensures forall i, j :: 0 <= i < |result| && 0 <= j < 6 ==>
    (i * 6 + j < |input| ==> result[i][j] == input[i * 6 + j]) &&  
    (i * 6 + j >= |input| ==> result[i][j] == '0')  
{
