datatype Error = Error(errorType: string)
datatype Route = Route(methods: seq<string>, route: string)
datatype ThreadState = Running | Stopped
class Routes {

    var threads: map<string, ThreadState>
    var middlewares: map<string, Middleware>


    ghost predicate Valid()
    {

        forall sid :: sid in middlewares ==> sid in threads
    }
    constructor()
    {
        threads := map[];
        middlewares := map[];
    }

    method OnConnect(sid: string)
    {
        threads := threads[sid := Running];
    }

    method OnDisconnect(sid: string)
    {
        if sid in middlewares {
            middlewares := map sid' | sid' in middlewares && sid' != sid :: middlewares[sid'];
        }
        threads := map sid' | sid' in threads && sid' != sid :: threads[sid'];
    }

    method ManageEvents(sid: string, streaming: bool, topics: seq<string>, reservoirSize: nat)
    {
        if streaming {
            var isRunning := sid in threads && threads[sid] == Running;
            if !isRunning {

                var mw := new Middleware(topics, reservoirSize);
                middlewares := middlewares[sid := mw];
                threads := threads[sid := Running];
            }
        } else {
            if sid in middlewares {

                threads := threads[sid := Stopped];

                middlewares := map sid' | sid' in middlewares && sid' != sid :: middlewares[sid'];
            }
        }
    }

    method ListRoutes(routes: seq<Route>) returns (result: seq<Route>)
    {
        result := routes;
    }
}
