    var topics: seq<string>
    var reservoirSize: nat

    constructor(topics': seq<string>, reservoirSize': nat)
        requires reservoirSize' > 0
        ensures topics == topics'
        ensures reservoirSize == reservoirSize'
        ensures fresh(this)
    {
        topics := topics';
