class Note {
  var timestamp: real
  var duration: real
  var pitch: real
  var velocity: real
  constructor(t: real, d: real, p: real, v: real)
  {
    timestamp := t;
    duration := d;
    pitch := p;
    velocity := v;
  }
}
type Population = seq<Note>
method RandomReal(min: real, max: real) returns (r: real)
{

  r := min + (max - min) / 2.0; 
}
method RandomInt(min: int, max: int) returns (r: int)
{

  r := min + (max - min) / 2; 
}
method GenRandPopulation(baseTimeDif: real) returns (population: Population)
{
  var currTimeStamp: real := 0.0;
  population := [];
  var i := 0;
  while i < maxPopSize
  {
    var duration := RandomReal(0.7, 5.0);  
    var pitch := RandomInt(minPitch, maxPitch);
    var velocity := RandomReal(0.0, 1.0);

    var timestamp := if i == 0 then 0.0 else baseTimeDif + currTimeStamp;
    var note := new Note(timestamp, duration, (pitch as real), velocity);

    population := population + [note];
    currTimeStamp := currTimeStamp + baseTimeDif;
    i := i + 1;
  }
}
method Breed(parentPop: Population) returns (childPop: Population)
{
  childPop := [];
  var i := 0;

  while i < |parentPop|
  {
    var nextIndex := if i + 1 < |parentPop| then i + 1 else 0;
    var curr := parentPop[i];
    var next := parentPop[nextIndex];

    var childNote := new Note(
      (curr.timestamp + next.timestamp) / 2.0,
      (curr.duration + next.duration) / 2.0,
      (curr.pitch + next.pitch) / 2.0,
      (curr.velocity + next.velocity) / 2.0
    );

    childPop := childPop + [childNote];
    i := i + 1;
  }
}
method GenCenter_direct(population: Population) returns (center: Note)
{
  var sumTimestamp: real := 0.0;
  var sumDuration: real := 0.0;
  var sumPitch: real := 0.0;
  var sumVelocity: real := 0.0;

  var i := 0;
  while i < |population|
  {
    sumTimestamp := sumTimestamp + population[i].timestamp;
    sumDuration := sumDuration + population[i].duration;
    sumPitch := sumPitch + population[i].pitch;
    sumVelocity := sumVelocity + population[i].velocity;
    i := i + 1;
  }

  var len := |population| as real;
  center := new Note(
    sumTimestamp / len,
    sumDuration / len,
    sumPitch / len,
    sumVelocity / len
  );
}
method GenAllRandPopulations(numPops: int) returns (populations: seq<Population>)
{
  populations := [];
  var i := 0;
  while i < numPops
  {
    var pop := GenRandPopulation(15.0);
    populations := populations + [pop];
    i := i + 1;
  }
}

