method Breed(parentPop: Population) returns (childPop: Population)
  requires |parentPop| > 0
  ensures |childPop| == |parentPop|
{
  childPop := [];
  var i := 0;

  while i < |parentPop|
    invariant 0 <= i <= |parentPop|
    invariant |childPop| == i
  {
    var nextIndex := if i + 1 < |parentPop| then i + 1 else 0;
    var curr := parentPop[i];
    var next := parentPop[nextIndex];

    var childNote := new Note(
      (curr.timestamp + next.timestamp) / 2.0,
      (curr.duration + next.duration) / 2.0,
      (curr.pitch + next.pitch) / 2.0,
      (curr.velocity + next.velocity) / 2.0
    );

    childPop := childPop + [childNote];
    i := i + 1;
