datatype Result<T> = Success(value: T) | Error(msg: string)
class {:autocontracts} HistogramProcessor {

  method ReadHistogram(filename: string) returns (result: Result<HistogramData>)
      |row| == |result.value.head|
  {
    var head := ["col1", "col2"];
    var values := map["row1" := [1.0, 2.0]];
    return Success(HistogramData(head, values));
  }

  method NormalizeValues(values: seq<real>) returns (normalized: seq<real>)
  {
    var sum := 0.0;
    var i := 0;
    var nonnegValues := [];


    while i < |values|
    {
      var val := if values[i] < 0.0 then 0.0 else values[i];
      nonnegValues := nonnegValues + [val];
      sum := sum + val;
      i := i + 1;
    }

    if sum == 0.0 {
      normalized := [];
      i := 0;
      while i < |values|
      {
        normalized := normalized + [0.0];
        i := i + 1;
      }
      return normalized;
    }

    normalized := [];
    i := 0;
    while i < |values|
    {
      var normalizedValue := nonnegValues[i] / sum;
      normalized := normalized + [normalizedValue];
      i := i + 1;
    }
  }

  method CalculateHistogramDifference(
    hist1: map<string, real>,
    hist2: map<string, real>,
    threshold: real
  ) returns (result: map<string, real>)
      (k in hist1 && k in hist2 ==> result[k] == hist1[k] - hist2[k]) ||
      (k in hist1 && k !in hist2 ==> result[k] == hist1[k]) ||
      (k !in hist1 && k in hist2 ==> result[k] == -hist2[k])
  {
    result := map[];


    var keys1 := set k | k in hist1;
    var iter := keys1;
    while iter != {}
        k in result && result[k] == hist1[k] - (if k in hist2 then hist2[k] else 0.0)
    {
      var k :| k in iter;
      var diff := hist1[k];
      if k in hist2 {
        diff := diff - hist2[k];
      }
      result := result[k := diff];
      iter := iter - {k};
    }

    var keys2 := set k | k in hist2;
    iter := keys2 - keys1;
    while iter != {}
        (k in hist1 ==> result[k] == hist1[k] - (if k in hist2 then hist2[k] else 0.0)) &&
        (k !in hist1 && k in hist2 ==> result[k] == -hist2[k])
    {
      var k :| k in iter;
      result := result[k := -hist2[k]];
      iter := iter - {k};
    }
  }
}

