    ensures forall i :: 0 <= i < |values| ==> 0.0 <= normalized[i] <= 1.0
  {
    var sum := 0.0;
    var i := 0;
    var nonnegValues := [];


    while i < |values|
      invariant 0 <= i <= |values|
      invariant |nonnegValues| == i
      invariant forall k :: 0 <= k < i ==> nonnegValues[k] >= 0.0
      invariant sum >= 0.0
      invariant forall k :: 0 <= k < i ==> nonnegValues[k] <= sum
    {
      var val := if values[i] < 0.0 then 0.0 else values[i];
      nonnegValues := nonnegValues + [val];
      sum := sum + val;
      i := i + 1;
    }

    if sum == 0.0 {
      normalized := [];
      i := 0;
      while i < |values|
        invariant 0 <= i <= |values|
        invariant |normalized| == i
        invariant forall k :: 0 <= k < i ==> normalized[k] == 0.0
      {
        normalized := normalized + [0.0];
        i := i + 1;
      }
      return normalized;
    }

    normalized := [];
    i := 0;
    while i < |values|
      invariant 0 <= i <= |values|
      invariant |normalized| == i
      invariant forall k :: 0 <= k < i ==> 0.0 <= normalized[k] <= 1.0
      invariant sum > 0.0
      invariant forall k :: 0 <= k < i ==> nonnegValues[k] >= 0.0
      invariant forall k :: 0 <= k < i ==> nonnegValues[k] <= sum
    {
      assert nonnegValues[i] >= 0.0;  
      assert nonnegValues[i] <= sum;  
      var normalizedValue := nonnegValues[i] / sum;
      assert nonnegValues[i] >= 0.0 && sum > 0.0 ==> normalizedValue >= 0.0;
      assert 0.0 <= nonnegValues[i] && nonnegValues[i] <= sum && sum > 0.0;  
      assert normalizedValue <= 1.0;  
      normalized := normalized + [normalizedValue];
      i := i + 1;
    }
  }

  method CalculateHistogramDifference(
    hist1: map<string, real>,
    hist2: map<string, real>,
    threshold: real
  ) returns (result: map<string, real>)
    requires threshold > 0.0
    requires forall k :: k in hist1 ==> -1.0 <= hist1[k] <= 1.0
    requires forall k :: k in hist2 ==> -1.0 <= hist2[k] <= 1.0
    ensures forall k :: k in result ==> 
      (k in hist1 && k in hist2 ==> result[k] == hist1[k] - hist2[k]) ||
      (k in hist1 && k !in hist2 ==> result[k] == hist1[k]) ||
      (k !in hist1 && k in hist2 ==> result[k] == -hist2[k])
  {
    result := map[];


    var keys1 := set k | k in hist1;
    var iter := keys1;
    while iter != {}
      invariant iter <= keys1
      invariant forall k :: k in (keys1 - iter) ==>
        k in result && result[k] == hist1[k] - (if k in hist2 then hist2[k] else 0.0)
      invariant forall k :: k in result ==> k in (keys1 - iter)
    {
      var k :| k in iter;
      var diff := hist1[k];
      if k in hist2 {
        diff := diff - hist2[k];
      }
      result := result[k := diff];
      iter := iter - {k};
    }

    var keys2 := set k | k in hist2;
    iter := keys2 - keys1;
    while iter != {}
      invariant iter <= keys2 - keys1
      invariant forall k :: k in result ==>
        (k in hist1 ==> result[k] == hist1[k] - (if k in hist2 then hist2[k] else 0.0)) &&
        (k !in hist1 && k in hist2 ==> result[k] == -hist2[k])
      invariant forall k :: k in (keys2 - keys1 - iter) ==> k in result && result[k] == -hist2[k]
      invariant forall k :: k in result && k !in hist1 ==> k in (keys2 - iter)
    {
      var k :| k in iter;
      result := result[k := -hist2[k]];
      iter := iter - {k};
    }
  }
}

