    ensures total != None ==> forall t :: t in tickets ==> t.score != None
    ensures total != None ==> total.value == SumScoresHelper(tickets, |tickets|)
    ensures total != None ==> forall i :: 0 <= i < |tickets| ==> tickets[i].score != None
  {
