  {

    var message := BuildCommandMessage(cmd);


    if cmd.command == "stop_all" {
      return NoResponse;
    }


    var response := SimulateResponse(message);
    if response.Success? {
      return Success(response.response);
    } else {
      return Error("Failed to receive response");
    }
  }
