datatype Unit = K | M | G | T
const FACTORS: map<Unit, nat> := map[
  K := 1024,
  M := 1048576, 
  G := 1073741827,
  T := 1099511627776
]
function UnitToChar(u: Unit): char
{
  match u
  case K => 'K'
  case M => 'M'
  case G => 'G'
  case T => 'T'
}
predicate IsDigitString(s: string)
{
  forall i :: 0 <= i < |s| ==> '0' <= s[i] <= '9'
}
function StringToNat(s: string): nat
{
  if |s| == 1 then 
    (s[0] as int - '0' as int) as nat
  else
    10 * StringToNat(s[..|s|-1]) + (s[|s|-1] as int - '0' as int) as nat
}
method HumanToBytes(human: string) returns (bytes: nat)
{
  if IsDigitString(human) {
    return StringToNat(human);
  }

  var modifier := human[|human|-1];
  var numPart := human[..|human|-1];

  if |numPart| == 0 || !IsDigitString(numPart) {
    return 0;
  }

  var num := StringToNat(numPart);

  match modifier {
    case 'K' => bytes := num * FACTORS[K];
    case 'M' => bytes := num * FACTORS[M];
    case 'G' => bytes := num * FACTORS[G];
    case 'T' => bytes := num * FACTORS[T];
    case _ => bytes := 0; 
  }
}

