class {:autocontracts} DataProcessor {

    method NormalizeSequence(data: seq<real>) returns (result: seq<real>)
    requires |data| > 0
    requires data[0] != 0.0
    ensures |result| == |data|
    ensures result[0] == data[0]/data[0]
    ensures forall i :: 0 <= i < |data| ==> result[i] == data[i] / data[0]
    {
        result := [];
        var lambda0 := data[0];
        var i := 0;

        while i < |data|
        invariant 0 <= i <= |data|
        invariant |result| == i
        invariant forall k :: 0 <= k < i ==> result[k] == data[k] / lambda0
        {
            result := result + [data[i] / lambda0];
            i := i + 1;
        }
    }

    method ProcessDataSet(dataset: DataSet) returns (normalizedSingle: seq<real>, normalizedDouble: seq<real>)
    requires |dataset.single| > 0 && |dataset.double| > 0
    requires dataset.single[0] != 0.0
    requires dataset.double[0] != 0.0
    ensures |normalizedSingle| == |dataset.single|
    ensures |normalizedDouble| == |dataset.double|
    {
        normalizedSingle := NormalizeSequence(dataset.single);
        normalizedDouble := NormalizeSequence(dataset.double);
    }

    method Linspace(start: real, stop: real, num: nat) returns (result: seq<real>)
    requires num > 1
    requires stop >= start
    ensures |result| == num
    ensures result[0] == start
    ensures result[num-1] == stop
    {
        result := [];
