    numFeatures := feat.Length1;
  }
  method StandardizeFeatures() returns (scaledFeatures: array2<real>)
  {
    scaledFeatures := new real[features.Length0, features.Length1];
    var means := CalculateColumnMeans();
    var stds := CalculateColumnStds(means);

    var i := 0;
    while i < features.Length0
    {
      var j := 0;
      while j < features.Length1
      {
        if stds[j] != 0.0 {
          scaledFeatures[i,j] := (features[i,j] - means[j]) / stds[j];
        } else {
          scaledFeatures[i,j] := 0.0;
        }
        j := j + 1;
      }
      i := i + 1;
    }
  }
  method CalculateColumnMeans() returns (means: array<real>)
  {
    means := new real[features.Length1];

    var j := 0;
    while j < features.Length1
    {
      var sum := 0.0;
      var i := 0;
      while i < features.Length0
      {
        sum := sum + features[i,j];
        i := i + 1;
      }
      means[j] := sum / (features.Length0 as real);
      j := j + 1;
    }
  }
  method CalculateColumnStds(means: array<real>) returns (stds: array<real>)
  {
    stds := new real[features.Length1];

    var j := 0;
    while j < features.Length1
    {
      var sumSquares := 0.0;
      var i := 0;
      while i < features.Length0
      {
        var diff := features[i,j] - means[j];
        sumSquares := sumSquares + diff * diff;
        i := i + 1;
      }
      var sqrtVal := 0.0;
      if sumSquares <= 0.0 {
        sqrtVal := 1.0;
      } else {
        var denominator := (features.Length0 as real) - 1.0;
        if denominator > 0.0 {
          sqrtVal := NewtonSqrt(sumSquares / denominator);
        } else {
          sqrtVal := 1.0;
        }
      }
      stds[j] := sqrtVal;
      j := j + 1;
    }
  }
  function NewtonSqrt(x: real): real
  {
