    else
      NewtonSqrtHelper(x, x/2.0, 10)
  }
  function NewtonSqrtHelper(x: real, approx: real, iter: nat): real
  {
    if iter == 0 then
      approx
    else
      NewtonSqrtHelper(x, (approx + x/approx)/2.0, iter-1)
  }
