datatype Level = Easy | Medium | Hard
class Stopwatch {

  var totalMilliseconds: int
  var acceptCount: int
  var totalCount: int
  var score: real
  var currentLevel: Level

  predicate Valid()
  {
    totalMilliseconds >= 0 &&
    acceptCount >= 0 &&
    totalCount >= 0 &&
    acceptCount <= totalCount &&
    0.0 <= score <= 100.0
  }

  constructor()
  {
    totalMilliseconds := 0;
    acceptCount := 0;
    totalCount := 0;
    score := 0.0;
    currentLevel := Easy;
  }

  function FormatTime(): (result: string)
  {
    var minutes := totalMilliseconds / 600;  
    var seconds := (totalMilliseconds / 10) % 60;
    var decisecond := totalMilliseconds % 10;

    match currentLevel
    case Easy => 
      if seconds >= 10 then
        Itos(minutes) + ":" + Itos(seconds) + "." + Itos(decisecond)
      else
        Itos(minutes) + ":0" + Itos(seconds) + "." + Itos(decisecond)
    case Medium =>
      if seconds >= 10 then
        Itos(minutes) + ":" + Itos(seconds) + ".*"
      else
        Itos(minutes) + ":0" + Itos(seconds) + ".*"
    case Hard =>
      Itos(minutes) + ":" + Itos(seconds/10) + "*.*"
  }

  static function Itos(n: int): string
  {
    if n < 10 then [((48 + n) as char)]
    else Itos(n/10) + [((48 + (n%10)) as char)]
  }

  method Update()
  {
    totalMilliseconds := totalMilliseconds + 1;
  }

  method Judge()
  {
    totalCount := totalCount + 1;
    if totalMilliseconds > 10 && totalMilliseconds % 10 == 0 {
      acceptCount := acceptCount + 1;
    }
    score := (acceptCount as real / totalCount as real) * 100.0;
  }

  method Reset()
  {
    totalMilliseconds := 0;
    acceptCount := 0;
    totalCount := 0;
    score := 0.0;
  }

  method SetLevel(level: Level)
  {
    currentLevel := level;
  }
}

