  {
    var sumX := 0;
    var sumY := 0;

    var i := 0;
    while i < numVertices 
    {
      sumX := sumX + vertices[i].x;
      sumY := sumY + vertices[i].y;
      i := i + 1;
    }
    center := new Point(sumX / numVertices, sumY / numVertices);
  }

  method SquaredDistance(p1: Point?, p2: Point?) returns (dist: int)
  {
    var dx := p1.x - p2.x;
    var dy := p1.y - p2.y;
    dist := dx * dx + dy * dy;
  }

  method IsRegular(tolerance: real) returns (regular: bool)
  {
    var edges: array<int> := new int[numVertices];
    var i := 0;

    while i < numVertices
    {
      var next := if i == numVertices - 1 then 0 else i + 1;
      var dist := SquaredDistance(vertices[i], vertices[next]);
      edges[i] := dist;
      i := i + 1;
