    var minEdge := edges[0];
    var maxEdge := edges[0];
    i := 1;

    while i < numVertices
    {
      if edges[i] < minEdge { minEdge := edges[i]; }
      if edges[i] > maxEdge { maxEdge := edges[i]; }
      i := i + 1;
    }
    regular := minEdge > 0 && (maxEdge as real) / (minEdge as real) <= tolerance;
  }
