    var i := 0;

    while i < numVertices
      invariant 0 <= i <= numVertices
      invariant edges.Length == numVertices
    {
      var next := if i == numVertices - 1 then 0 else i + 1;
      var dist := SquaredDistance(vertices[i], vertices[next]);
      edges[i] := dist;
      i := i + 1;
    }
    var minEdge := edges[0];
    var maxEdge := edges[0];
    i := 1;

    while i < numVertices
      invariant 1 <= i <= numVertices
      invariant minEdge <= maxEdge
    {
      if edges[i] < minEdge { minEdge := edges[i]; }
      if edges[i] > maxEdge { maxEdge := edges[i]; }
      i := i + 1;
    }
    regular := minEdge > 0 && (maxEdge as real) / (minEdge as real) <= tolerance;
  }
}
method TranslatePolygon(poly: Polygon?, dx: int, dy: int) returns (newPoly: Polygon?)
  requires poly != null
  requires poly.numVertices > 0
  requires poly.numVertices <= poly.vertices.Length
  requires forall i :: 0 <= i < poly.vertices.Length ==> poly.vertices[i] != null
  ensures newPoly != null
  ensures newPoly.numVertices == poly.numVertices
  ensures fresh(newPoly)
  ensures fresh(newPoly.vertices)
{
  newPoly := new Polygon(poly.numVertices);
  newPoly.numVertices := poly.numVertices;
  var i := 0;
  while i < poly.numVertices
    invariant 0 <= i <= poly.numVertices
    invariant newPoly.numVertices == poly.numVertices
    modifies newPoly.vertices
  {
    newPoly.vertices[i] := new Point(poly.vertices[i].x + dx, poly.vertices[i].y + dy);
    i := i + 1;
  }
}

