method TravelTime(from: Location, to: Location) returns (time: int)
{
  var dx := from.x - to.x;
  var dy := from.y - to.y;
  time := dx * dx + dy * dy;
  time := SqrtCeil(time);
}
method SqrtCeil(n: int) returns (result: int)
{
  if n == 0 {
    return 0;
  }
  result := 1;
  while result * result < n
  {
    result := result + 1;
  }
}
method AssignWarehousesToOrders(orders: seq<Order>, warehouses: seq<Warehouse>)
{
  var i := 0;
  while i < |orders|
  {
    var minDist := -1;
    var bestWH := 0;
    var j := 0;
    while j < |warehouses|
    {
      var dist := TravelTime(orders[i].location, warehouses[j].location);
      if minDist == -1 || dist < minDist {
        minDist := dist;
        bestWH := j;
      }
      j := j + 1;
    }
    orders[i].assignedWarehouse := warehouses[bestWH];
    i := i + 1;
  }
}

