method SqrtCeil(n: int) returns (result: int)
  requires n >= 0
  ensures result >= 0
  ensures result * result >= n
  ensures (result - 1) * (result - 1) < n || result == 0
{
  if n == 0 {
    return 0;
  }
  result := 1;
  while result * result < n
    invariant result >= 1
    invariant (result - 1) * (result - 1) < n
    decreases n - result * result
  {
