method AssignWarehousesToOrders(orders: seq<Order>, warehouses: seq<Warehouse>)
  requires |warehouses| > 0
  modifies orders, set o | 0 <= o < |orders| :: orders[o]
  ensures forall o | 0 <= o < |orders| :: orders[o].assignedWarehouse != null
