    invariant forall k | 0 <= k < |orders| :: orders[k] in orders
    invariant forall k | 0 <= k < i :: orders[k].assignedWarehouse in warehouses
    modifies set o | 0 <= o < |orders| :: orders[o]
  {
    var minDist := -1;
    var bestWH := 0;
    var j := 0;
    while j < |warehouses|
      invariant 0 <= j <= |warehouses|
      invariant minDist == -1 ==> j == 0
      invariant minDist >= 0 ==> 0 <= bestWH < j
      invariant bestWH < |warehouses|
      invariant forall k | 0 <= k < i :: orders[k].assignedWarehouse != null
      invariant forall k | 0 <= k < i :: orders[k].assignedWarehouse in warehouses
    {
