class WorkCenter {
    var id: int
    var qtyTerminalsReceived: int
    var qtyTerminalsUsed: int
    var region: string
    constructor(id': int, region': string)
    {
        id := id';
        qtyTerminalsReceived := 0;
        qtyTerminalsUsed := 0;
        region := region';
    }

    function GetAvailableTerminals(): int
    {
        qtyTerminalsReceived - qtyTerminalsUsed
    }
}
class Expedition {
    var id: int
    var qtyTerminals: int
    var wasCanceled: bool
    var workCenter: WorkCenter
    constructor(id': int, qty: int, wc: WorkCenter)
    {
        id := id';
        qtyTerminals := qty;
        wasCanceled := false;
        workCenter := wc;
    }
}
class ExpeditionManager {
    var expeditions: seq<Expedition>
    var repr: set<object>
    predicate Valid()
    {
        this in repr &&
        (forall e :: e in expeditions ==> e in repr && e.workCenter in repr)
    }
    constructor()
    {
        expeditions := [];
        repr := {this};
    }

    method CreateExpedition(id: int, qty: int, workCenter: WorkCenter) returns (exp: Expedition)
    {
        exp := new Expedition(id, qty, workCenter);
        expeditions := expeditions + [exp];
        repr := repr + {exp};
        workCenter.qtyTerminalsReceived := workCenter.qtyTerminalsReceived + qty;
    }

    method CancelExpedition(exp: Expedition)
                old(exp.workCenter.qtyTerminalsReceived) - (if old(!exp.wasCanceled) then exp.qtyTerminals else 0)
    {
        if !exp.wasCanceled {
            exp.wasCanceled := true;
            exp.workCenter.qtyTerminalsReceived := 
                exp.workCenter.qtyTerminalsReceived - exp.qtyTerminals;
        }
    }

    method FindExpedition(id: int) returns (exp: Expedition?)
    {
        var i := 0;
        while i < |expeditions|
        {
            if expeditions[i].id == id {
                return expeditions[i];
            }
            i := i + 1;
        }
        return null;
    }
}

