        workCenter.qtyTerminalsReceived := workCenter.qtyTerminalsReceived + qty;
    }

    method CancelExpedition(exp: Expedition)
        requires exp in expeditions
        requires Valid()
        modifies exp, exp.workCenter
        ensures exp.wasCanceled
        ensures exp.workCenter.qtyTerminalsReceived == 
                old(exp.workCenter.qtyTerminalsReceived) - (if old(!exp.wasCanceled) then exp.qtyTerminals else 0)
        ensures Valid()
    {
        if !exp.wasCanceled {
            exp.wasCanceled := true;
            exp.workCenter.qtyTerminalsReceived := 
                exp.workCenter.qtyTerminalsReceived - exp.qtyTerminals;
        }
    }

    method FindExpedition(id: int) returns (exp: Expedition?)
        requires Valid()
        ensures exp != null ==> exp in expeditions && exp.id == id
        ensures exp == null ==> forall e :: e in expeditions ==> e.id != id
    {
        var i := 0;
        while i < |expeditions|
            invariant 0 <= i <= |expeditions|
            invariant forall k :: 0 <= k < i ==> expeditions[k].id != id
        {
            if expeditions[i].id == id {
                return expeditions[i];
            }
            i := i + 1;
        }
        return null;
    }
}

