    {
        if arch != "vgg16" && arch != "resnet18" {
            return false;
        }

        learningRate := lr;
        hiddenUnits := hidden;
        return true;
    }

    method Train(data: array<real>, labels: array<int>, epochs: nat) returns (loss: real)
        requires data.Length > 0 && labels.Length > 0
        requires data.Length == labels.Length
        requires epochs > 0
        modifies this`trainingSteps, this`modelState
        ensures trainingSteps > old(trainingSteps)
        ensures loss >= 0.0
    {
        var currentEpoch := 0;
        loss := 0.0;
        var oldTrainingSteps := trainingSteps;
        trainingSteps := trainingSteps + 1; 

        while currentEpoch < epochs
            invariant 0 <= currentEpoch <= epochs
            invariant trainingSteps > oldTrainingSteps
            invariant loss >= 0.0
        {
            var i := 0;
            while i < data.Length
                invariant 0 <= i <= data.Length
                invariant loss >= 0.0
                invariant trainingSteps > oldTrainingSteps
            {

                trainingSteps := trainingSteps + 1;
                var stepLoss: real;
                stepLoss := if data[i] >= 0.0 then 0.1 else 0.2; 
                loss := loss + stepLoss;
                i := i + 1;
            }
            currentEpoch := currentEpoch + 1;
        }
    }

    function ComputeLoss(x: real, lbl: int): real
        ensures ComputeLoss(x, lbl) >= 0.0
    {
        if x >= 0.0 then 0.1 else 0.2
    }

