        requires input.Length > 0
        requires topK > 0 && topK <= 102  
        ensures fresh(probs) && fresh(classes)
        ensures probs.Length == topK
        ensures classes.Length == topK
        ensures forall i :: 0 <= i < probs.Length ==> 0.0 <= probs[i] <= 1.0
    {
        probs := new real[topK];
