method ObjectDef(D: real, DRatio: real, N: nat) returns (result: Matrix)
{

  result := new Matrix(N, N);


  var step: real := D / (N as real);
  var offset: real := D / (2.0 * (N as real));


  var x0: real := 0.3 * D * DRatio;
  var y0: real := 0.1 * D * DRatio;
  var radius: real := D * DRatio / 2.0;


  var i := 0;
  while i < N
  {
    var j := 0;
    while j < N
    {

      var x: real := -D/2.0 + (j as real)*step + offset;
      var y: real := -D/2.0 + (i as real)*step + offset;


      var r: real := Sqrt(x*x + y*y);


      var dx := x-x0;
      var dy := y-y0;
      var dist: real := Sqrt(dx*dx + dy*dy);


      if r < radius && dist >= radius/50.0 {
        result.data[i,j] := 1.0;
      } else {
        result.data[i,j] := 0.0;
      }

      j := j + 1;
    }
    i := i + 1;
