    ensures forall m :: m in filtered ==> DateInRange(m.date, start, end)
    ensures |filtered| <= |measurements|
{
    filtered := [];
    var i := 0;

    while i < |measurements|
        invariant 0 <= i <= |measurements|
        invariant forall m :: m in filtered ==> DateInRange(m.date, start, end)
        invariant |filtered| <= i
    {
        if DateInRange(measurements[i].date, start, end) {
            filtered := filtered + [measurements[i]];
        }
        i := i + 1;
    }
}
predicate DateInRange(d: Date, start: Date, end: Date)
{
    DateLessOrEqual(start, d) && DateLessOrEqual(d, end)
}
predicate ValidDateRange(start: Date, end: Date)
{
    DateLessOrEqual(start, end)
}
predicate DateLessOrEqual(d1: Date, d2: Date)
{
    d1.year < d2.year ||
    (d1.year == d2.year && d1.month < d2.month) ||
    (d1.year == d2.year && d1.month == d2.month && d1.day <= d2.day)
}

