class ThermodynamicCalculator {
    const R: real := 8.314    
    const gamma: real         
    const M: real            

    constructor(g: real, m: real)
    requires g > 1.0  
    requires m > 0.0  
    ensures gamma == g && M == m
    {
        gamma := g;
        M := m;
    }


    method CalcIsentropicPressure(v1: real, v2: real, P1: real) returns (P2: real)
    requires v1 > 0.0 && v2 > 0.0 && P1 > 0.0
    ensures P2 > 0.0
    {
        P2 := P1 * (v1/v2) * (v1/v2) * (v1/v2);  
        assert P2 > 0.0;
    }


    method CalcIsentropicTemperature(T1: real, v1: real, v2: real) returns (T2: real)
    requires T1 > 0.0 && v1 > 0.0 && v2 > 0.0
    ensures T2 > 0.0
    {
        T2 := T1 * (v1/v2) * (v1/v2);  
        assert T2 > 0.0;
    }


    method CalcWork(P: array<real>, v: array<real>) returns (W: real)
    requires P.Length == v.Length
    requires P.Length > 1
    requires forall i :: 0 <= i < P.Length ==> P[i] > 0.0
    requires forall i :: 0 <= i < v.Length ==> v[i] > 0.0
    ensures W >= 0.0
    {
        W := 0.0;
        var i := 0;

        while i < P.Length - 1
        invariant 0 <= i <= P.Length - 1
        invariant W >= 0.0
        decreases P.Length - i
        {
            var dW := (P[i] + P[i+1])/2.0 * (v[i+1] - v[i]);
            W := W + if dW >= 0.0 then dW else -dW;  
            i := i + 1;
        }
    }
}
method CalculateIdealGasCycle(
    T1: real, P1: real, P3: real, T4: real, a: real,
    calculator: ThermodynamicCalculator
) returns (cycle: array<StatePoint>)
requires T1 > 0.0 && P1 > 0.0 && P3 > 0.0 && T4 > 0.0 && a > 1.0
requires calculator.M > 0.0
ensures cycle.Length == 5
{
    var v1 := calculator.R * T1 / (calculator.M * P1);
    assert v1 > 0.0;
    var point0 := new StatePoint(T1, P1, v1);

    var v2 := v1 / a;
    assert v2 > 0.0;
    var P2 := calculator.CalcIsentropicPressure(v1, v2, P1);
    var T2 := calculator.CalcIsentropicTemperature(T1, v1, v2);
    var point1 := new StatePoint(T2, P2, v2);

    var T3 := calculator.M * P3 * v2 / calculator.R;
    assert T3 > 0.0;
    var point2 := new StatePoint(T3, P3, v2);

    var v4 := calculator.R * T4 / (calculator.M * P3);
    assert v4 > 0.0;
    var point3 := new StatePoint(T4, P3, v4);

    var P5 := calculator.CalcIsentropicPressure(v4, v1, P3);
    var T5 := calculator.CalcIsentropicTemperature(T4, v4, v1);
    var point4 := new StatePoint(T5, P5, v1);

    cycle := new StatePoint[5] [point0, point1, point2, point3, point4];
}

