  var num := 65;  
  var i := 0;
  while i < n
  {
    var j := 0;
    while j <= i
    {
      if 0 <= num - j < 128 {
        print(((num - j) as char), " ");
      } else {
        print("? ");
      }
      j := j + 1;
    }
    print("\n");
    i := i + 1;
  }
}
