datatype DateTime = DateTime(timestamp: int)
datatype TripRecord = TripRecord(id: string, taxiId: string, endTime: DateTime)
datatype GroupedTrip = GroupedTrip(taxiId: string, trips: seq<TripRecord>)
function IsLaterThan(d1: DateTime, d2: DateTime): bool {
    d1.timestamp > d2.timestamp
}
function TimeDifference(d1: DateTime, d2: DateTime): int {
    d1.timestamp - d2.timestamp
}
method CalculateLoss(trips: seq<TripRecord>) returns (loss: int)
    requires |trips| > 0  
    requires forall i, j :: 0 <= i < j < |trips| ==>  
        !IsLaterThan(trips[i].endTime, trips[j].endTime)
    ensures loss >= 0  
{
    loss := 0;
    var i := 1;

    while i < |trips|
        invariant 1 <= i <= |trips|
        invariant loss >= 0
        invariant loss == SumPositiveDeltas(trips, i)
        decreases |trips| - i
    {
        var timeDiff := TimeDifference(
            trips[i].endTime, 
            trips[i-1].endTime
        );

        if timeDiff > 0 {
            loss := loss + timeDiff;
        }
        i := i + 1;
    }
}
function SumPositiveDeltas(trips: seq<TripRecord>, upTo: int): int
    requires 0 < upTo <= |trips|
{
    if upTo == 1 then
        0
    else
        var diff := TimeDifference(trips[upTo-1].endTime, trips[upTo-2].endTime);
        SumPositiveDeltas(trips, upTo-1) + (if diff > 0 then diff else 0)
}
method GroupTripsByTaxi(trips: seq<TripRecord>) returns (grouped: seq<GroupedTrip>)
    requires |trips| > 0
    ensures |grouped| > 0
    ensures forall g :: g in grouped ==> |g.trips| > 0
{

    var allTrips := trips;
    var taxiId := trips[0].taxiId;
    grouped := [GroupedTrip(taxiId, allTrips)];
}

