datatype Handler = PostTweetHandler | TweetPageHandler(id: int)
datatype URL = Url(path: string)
datatype Response = Response(status: int, content: string)
datatype Option<T> = None | Some(value: T)
class UrlRouter {
  var routes: map<string, Handler>
  constructor()
  {
    routes := map[];
  }
  method RegisterRoute(path: string, handler: Handler)
            p in routes && routes[p] == old(routes[p])
  {
    routes := routes[path := handler];
  }
  method MatchUrl(url: URL) returns (handler: Option<Handler>)
  {
    if url.path in routes {
      return Some(routes[url.path]);
    }
    return None;
  }
  method HandleRequest(url: URL) returns (response: Response)
  {
    var handlerOpt := MatchUrl(url);

    if handlerOpt.None? {
      return Response(404, "Not Found");
    } else {
      var handler := handlerOpt.value;
      match handler {
        case PostTweetHandler => 
          return Response(200, "Tweet Posted");
        case TweetPageHandler(id) => 
          return Response(200, "Showing Tweet " + IntToString(id));
      }
    }
  }

  function IntToString(n: int): string
  {
    if n == 0 then "0"
    else if n < 0 then "-" + IntToStringHelper(-n)
    else IntToStringHelper(n)
  }

  function IntToStringHelper(n: int): string
  {
    if n == 0 then ""
    else IntToStringHelper(n/10) + [((n%10) + 48) as char]
  }
}
method Main()
{
  var router := new UrlRouter();

  router.RegisterRoute("post_tweet/", PostTweetHandler);
  router.RegisterRoute("tweet_page/123/", TweetPageHandler(123));
  var response := router.HandleRequest(Url("post_tweet/"));
}

