method AggregateEnergyReadings(dailyReadings: seq<EnergyReading>) returns (weeklyReadings: seq<WeeklyReading>)
  requires |dailyReadings| > 0
  requires forall i :: 0 <= i < |dailyReadings| ==> dailyReadings[i].energy >= 0.0
  requires forall i :: 0 <= i < |dailyReadings| ==> dailyReadings[i].day >= 0
  requires forall i, j :: 0 <= i < j < |dailyReadings| ==> dailyReadings[i].day <= dailyReadings[j].day
  ensures |weeklyReadings| > 0 
  ensures forall i, j :: 0 <= i < j < |weeklyReadings| ==> 
    weeklyReadings[i].weekStart < weeklyReadings[j].weekStart
  ensures forall i :: 0 <= i < |weeklyReadings| ==> weeklyReadings[i].totalEnergy >= 0.0
{
  var currentWeek := GetWeekNumber(dailyReadings[0].day);
  var weeklySum := dailyReadings[0].energy;
  var result: seq<WeeklyReading> := [];
  var i := 1;

  while i < |dailyReadings|
    invariant 1 <= i <= |dailyReadings|
    invariant weeklySum >= 0.0
    invariant currentWeek == GetWeekNumber(dailyReadings[i-1].day)
    invariant forall k, l :: 0 <= k < l < |result| ==> 
      result[k].weekStart < result[l].weekStart
    invariant forall k :: 0 <= k < |result| ==>
      result[k].weekStart < currentWeek
    invariant forall k :: 0 <= k < |result| ==>
      result[k].totalEnergy >= 0.0
    invariant forall k :: 0 <= k < i ==> GetWeekNumber(dailyReadings[k].day) <= currentWeek
    invariant forall k :: 0 <= k < i-1 ==> 
      GetWeekNumber(dailyReadings[k].day) <= GetWeekNumber(dailyReadings[k+1].day)
    decreases |dailyReadings| - i
  {
    var reading := dailyReadings[i];
    var readingWeek := GetWeekNumber(reading.day);

    if readingWeek == currentWeek {
      weeklySum := weeklySum + reading.energy;
    } else {
      assert readingWeek > currentWeek;
      var newReading := new WeeklyReading(currentWeek, weeklySum);

      result := result + [newReading];

      currentWeek := readingWeek;
      weeklySum := reading.energy;
    }

    i := i + 1;
