  assert forall i :: 0 <= i < |weeklyReadings| ==> weeklyReadings[i].totalEnergy >= 0.0;
  assert forall i, j :: 0 <= i < j < |weeklyReadings| ==> 
    weeklyReadings[i].weekStart < weeklyReadings[j].weekStart;
}
