method ConvertToDigitArray(n: int) returns (arr: array<int>)
{
    arr := new int[5];
    arr[0] := n % 10;
    arr[1] := (n / 10) % 10;
    arr[2] := (n / 100) % 10;
    arr[3] := (n / 1000) % 10;
    arr[4] := (n / 10000) % 10;
}
method AddDigitArrays(a: array<int>, b: array<int>) returns (sum: array<int>)
{
    sum := new int[5];
    var carry := 0;
    var i := 0;

    while i < 5
    {
        var digitSum := a[i] + b[i] + carry;
        sum[i] := digitSum % 10;
        carry := digitSum / 10;
        i := i + 1;
    }
}
