predicate IsValidPosition(grid: Grid, pos: Position, n: int)
{
  0 <= pos.row < grid.data.Length0 && 
  0 <= pos.col < grid.data.Length1 &&
  grid.data[pos.row, pos.col] == '.'
}
method CountPaths(grid: Grid) returns (count: int)
    0 <= i < grid.data.Length0 && 0 <= j < grid.data.Length1 ==> 
    (grid.data[i,j] == '.' || grid.data[i,j] == 'X')
{
  var n := grid.data.Length1;


  var totalPaths := Pow2(n-1) - 1;
  count := 0;
  var pathNum := 0;
  while pathNum <= totalPaths
  {
    var pos := Position(0, 0);
    var valid := IsValidPosition(grid, pos, n);


    var remainingMoves := pathNum;
    var moveIndex := 0;
    while moveIndex < n-1 && valid
    {
      var moveRight := remainingMoves % 2 == 1;
      remainingMoves := remainingMoves / 2;

      if moveRight {

        var newPos := Position(pos.row, pos.col + 1);
        if !IsValidPosition(grid, newPos, n) {
          valid := false;
        }
        pos := newPos;
