      moveIndex := moveIndex + 1;
    }

    if valid && pos.row == 1 && pos.col == n-1 {
      count := count + 1;
    }
    pathNum := pathNum + 1;
  }

  if count == 0 {
    count := -1;
  }
}
function Pow2(n: nat): nat
{
  if n == 0 then 1
  else 2 * Pow2(n-1)
