    }

    function LinearFunction(X: seq<seq<real>>, Theta: seq<real>): seq<real>
        requires forall i :: 0 <= i < |X| ==> |X[i]| == |Theta|
        ensures |LinearFunction(X, Theta)| == |X|
    {
        if |X| == 0 then []
        else [DotProduct(X[0], Theta)] + LinearFunction(X[1..], Theta)
    }

    function MSELoss(X: seq<seq<real>>, Theta: seq<real>, y_true: seq<real>): real
        requires |X| == |y_true|
        requires |X| > 0
        requires forall i :: 0 <= i < |X| ==> |X[i]| == |Theta|
        ensures MSELoss(X, Theta, y_true) >= 0.0
    {
        var y_pred := LinearFunction(X, Theta);
        SumOfSquaredDifferences(y_pred, y_true) / |y_true| as real
    }

    function SumOfSquaredDifferences(v1: seq<real>, v2: seq<real>): real
        requires |v1| == |v2|
        ensures SumOfSquaredDifferences(v1, v2) >= 0.0
    {
        if |v1| == 0 then 0.0
        else (v1[0] - v2[0]) * (v1[0] - v2[0]) + SumOfSquaredDifferences(v1[1..], v2[1..])
    }

    method GradientFunction(Theta: seq<real>, X: seq<seq<real>>, y_true: seq<real>) returns (grad: seq<real>)
        requires |X| > 0
        requires forall i :: 0 <= i < |X| ==> |X[i]| == |Theta|
        requires |X| == |y_true|
        ensures |grad| == |Theta|
    {
        var y_pred := LinearFunction(X, Theta);
        grad := seq(|Theta|, i => 0.0);

        var n := |X|;
        var m := |Theta|;

        var i := 0;
        while i < m
            invariant 0 <= i <= m
            invariant |grad| == m
        {
            var sum := 0.0;
            var j := 0;
            while j < n
                invariant 0 <= j <= n
            {
                sum := sum + (y_pred[j] - y_true[j]) * X[j][i];
                j := j + 1;
            }
            grad := grad[i := 2.0 * sum / (n as real)];
            i := i + 1;
        }
    }

    method GradientDescent(Theta: seq<real>, X: seq<seq<real>>, y: seq<real>, alpha: real, iters: nat) returns (theta: seq<real>)
        requires |X| > 0
        requires forall i :: 0 <= i < |X| ==> |X[i]| == |Theta|
        requires |X| == |y|
        requires alpha > 0.0
        ensures |theta| == |Theta|
    {
        theta := Theta;
        var i := 0;
        while i < iters
            invariant 0 <= i <= iters
            invariant |theta| == |Theta|
        {
            var grad := GradientFunction(theta, X, y);
            var j := 0;
            var new_theta := seq(|theta|, _ => 0.0);
            while j < |theta|
                invariant 0 <= j <= |theta|
                invariant |new_theta| == |theta|
            {
                new_theta := new_theta[j := theta[j] - alpha * grad[j]];
                j := j + 1;
            }
            theta := new_theta;
            i := i + 1;
        }
    }
}

