class Matrix {
  var rows: nat
  var cols: nat
  var data: array2<real>

  constructor(r: nat, c: nat)
    requires r > 0 && c > 0 
    ensures rows == r && cols == c
    ensures fresh(data)
    ensures data.Length0 == rows && data.Length1 == cols
  {
    rows := r;
    cols := c;
    data := new real[r,c];
  }
}
class PRENHead {
  var inChannels: nat
  var outChannels: nat  
  var weights: Matrix  
  var bias: array<real>  

  constructor(in_ch: nat, out_ch: nat)
    requires in_ch > 0 && out_ch > 0
    ensures inChannels == in_ch && outChannels == out_ch
    ensures fresh(weights) && fresh(bias)
    ensures weights.rows == outChannels && weights.cols == inChannels
    ensures bias.Length == outChannels
  {
    inChannels := in_ch;
    outChannels := out_ch;
    weights := new Matrix(out_ch, in_ch);
    bias := new real[out_ch];
  }

  method Forward(x: Matrix, isTraining: bool) returns (result: Matrix)
    requires x.cols == inChannels  
    requires x.rows > 0 
    requires outChannels > 0
    requires x.data.Length0 == x.rows && x.data.Length1 == inChannels
    requires weights.data.Length0 == outChannels && weights.data.Length1 == inChannels
    requires bias.Length == outChannels
    ensures fresh(result)
    ensures result.rows == x.rows && result.cols == outChannels
  {

    result := new Matrix(x.rows, outChannels);


    var i := 0;
    while i < x.rows
      invariant 0 <= i <= x.rows
      invariant result.rows == x.rows && result.cols == outChannels
      invariant result.data.Length0 == x.rows && result.data.Length1 == outChannels
      modifies result.data
    {
      var j := 0;
      while j < outChannels
        invariant 0 <= j <= outChannels
        invariant 0 <= i < x.rows
        invariant result.data.Length0 == x.rows && result.data.Length1 == outChannels
        modifies result.data
      {
        var sum := 0.0;
        var k := 0;
        while k < inChannels
          invariant 0 <= k <= inChannels
          invariant 0 <= i < x.rows && 0 <= j < outChannels
          invariant x.data.Length0 == x.rows && x.data.Length1 == inChannels
          invariant weights.data.Length0 == outChannels && weights.data.Length1 == inChannels
        {
          sum := sum + x.data[i,k] * weights.data[j,k];
          k := k + 1;
        }
        result.data[i,j] := sum + bias[j];
        j := j + 1;
      }
      i := i + 1;
    }

    if !isTraining {
      ApplySoftmax(result);
    }
  }

  method ApplySoftmax(matrix: Matrix)
    requires matrix.rows > 0 && matrix.cols > 0
    requires matrix.data.Length0 == matrix.rows && matrix.data.Length1 == matrix.cols
    modifies matrix.data
    ensures matrix.data.Length0 == old(matrix.data.Length0) && matrix.data.Length1 == old(matrix.data.Length1)
  {
    var i := 0;
    while i < matrix.rows
      invariant 0 <= i <= matrix.rows
      invariant matrix.data.Length0 == old(matrix.data.Length0) && matrix.data.Length1 == old(matrix.data.Length1)
      modifies matrix.data
    {

      var maxVal := matrix.data[i,0];
      var j := 1;
      while j < matrix.cols
        invariant 1 <= j <= matrix.cols
        invariant 0 <= i < matrix.rows
        invariant matrix.data.Length0 == matrix.rows && matrix.data.Length1 == matrix.cols
      {
        if matrix.data[i,j] > maxVal {
          maxVal := matrix.data[i,j];
        }
        j := j + 1;
      }

      var sum := 0.0;
      j := 0;
      while j < matrix.cols
        invariant 0 <= j <= matrix.cols
        invariant 0 <= i < matrix.rows
        invariant matrix.data.Length0 == matrix.rows && matrix.data.Length1 == matrix.cols
        invariant sum >= 0.0
        invariant forall k :: 0 <= k < j ==> matrix.data[i,k] > 0.0
        invariant j > 0 ==> sum > 0.0
      {
        matrix.data[i,j] := Exp(matrix.data[i,j] - maxVal);
        sum := sum + matrix.data[i,j];
        j := j + 1;
      }

      assert matrix.cols > 0;  
      assert sum > 0.0;        

      j := 0;
      while j < matrix.cols
        invariant 0 <= j <= matrix.cols
        invariant 0 <= i < matrix.rows
        invariant matrix.data.Length0 == matrix.rows && matrix.data.Length1 == matrix.cols
        invariant sum > 0.0
      {
        matrix.data[i,j] := matrix.data[i,j] / sum;
        j := j + 1;
      }
      i := i + 1;
    }
  }

  function Exp(x: real): real
    ensures Exp(x) > 0.0
  {

    var result := 1.0 + x + (x * x) / 2.0 + (x * x * x) / 6.0 + (x * x * x * x) / 24.0;
    if result <= 0.0 then 0.000001 else result
  }
}

