  }
  method Generator(seed: array<real>) returns (output: array<real>)
  {
    var size := resolution * resolution * channels;
    output := new real[size];
    var factor := SumArray(seed, seed.Length) / (seed.Length as real);
    for i := 0 to output.Length
    {
      output[i] := Tanh(factor * (i as real) / (output.Length as real));
    }
  }
