    && serving <= ticket
    && (forall p ::  
      p in P && cs[p] != Thinking
      ==> serving <= t[p] < ticket
    )
    && (forall p, q ::  
      p in P && q in P && p != q && cs[p] != Thinking && cs[q] != Thinking
      ==> t[p] != t[q]
    )
    && (forall p ::  
      p in P && cs[p] == Eating
      ==> t[p] == serving
    )
  }

  constructor (processes: set<Process>)
  {
    P := processes;
    ticket, serving := 0, 0;  

    cs := map p | p in processes :: Thinking;  
    t := map p | p in processes :: 0;
  }


