class BoundingBox {
  var minLon: real
  var maxLon: real
  var minLat: real
  var maxLat: real

  constructor(minLon: real, maxLon: real, minLat: real, maxLat: real)
    requires minLon <= maxLon
    requires minLat <= maxLat
    requires -180.0 <= minLon <= 180.0
    requires -180.0 <= maxLon <= 180.0
    requires -90.0 <= minLat <= 90.0
    requires -90.0 <= maxLat <= 90.0
    ensures this.minLon == minLon && this.maxLon == maxLon
    ensures this.minLat == minLat && this.maxLat == maxLat
  {
    this.minLon := minLon;
    this.maxLon := maxLon;
    this.minLat := minLat;
    this.maxLat := maxLat;
  }
  method Buffer(amount: real) returns (newBox: BoundingBox)
    requires amount >= 0.0
    requires minLon - amount >= -180.0 && maxLon + amount <= 180.0
    requires minLat - amount >= -90.0 && maxLat + amount <= 90.0
    requires minLon <= maxLon 
    requires minLat <= maxLat 
    ensures newBox.minLon == minLon - amount
    ensures newBox.maxLon == maxLon + amount
    ensures newBox.minLat == minLat - amount
