class MonteCarloPI {
    function RandomDouble(): real
      ensures 0.0 <= RandomDouble() < 1.0
    {
      0.5
    }
    method SingleIteration() returns (inCircle: bool)
      ensures true
    {
      var x := RandomDouble();
      var y := RandomDouble();
      inCircle := x * x + y * y < 1.0;
    }
    method CalculatePI(n: nat) returns (pi: real)
      requires n > 0
      ensures 0.0 <= pi <= 4.0
    {
      var circleCount := 0;
      var i := 0;
      while i < n
        invariant 0 <= i <= n
        invariant 0 <= circleCount <= i
      {
        var isInCircle := SingleIteration();
        if isInCircle {
          circleCount := circleCount + 1;
        }
        i := i + 1;
      }
      pi := (circleCount as real / n as real) * 4.0;
    }
    method RunSimulation(batchSize: nat) decreases *
      requires batchSize > 0
    {
      var iterations := 0;

      while true
        decreases *
      {
        var approx := CalculatePI(batchSize);
        iterations := iterations + batchSize;
        print "Iterations: ", iterations, "   ", approx, "\n";
      }
    }
}

