    method BasicComparisons() 

    {

        print "Is 5 less than 3? ", 5 < 3, "\n";
        print "Is 5 greater than 3? ", 5 > 3, "\n";
        print "Is 5 greater than or equal to 3? ", 5 >= 3, "\n";
        print "Is 5 greater than 5? ", 5 > 5, "\n";
        print "Is 5 greater than or equal to 5? ", 5 >= 5, "\n";
        print "Is 5 equal to 5? ", 5 == 5, "\n";
        print "Is 5 equal to 3? ", 5 == 3, "\n";
        print "Is 5 not equal to 3? ", 5 != 3, "\n";
    }

    method BooleanOperations()
    {
        var a := true;
        var b := false;

        print "a is ", a, "\n";
        print "b is ", b, "\n";


        print "a and a: ", a && a, "\n";
        print "a and b: ", a && b, "\n";
        print "b and b: ", b && b, "\n";


        print "a or a: ", a || a, "\n";
