datatype Date = Date(year: int, month: int)
datatype Account = Account(
  pesel: string,
  firstName: string,
  lastName: string,
  income: real,
  baseCurrency: string
)
datatype CreditParameters = CreditParameters(
  currency: string,
  cost: real,
  startDate: Date,
  endDate: Date
)
const VALID_CURRENCIES: seq<string> := ["PLN", "USD", "EUR", "CHF", "GBP"]
predicate IsValidDate(d: Date)
{
  && d.month >= 1 
  && d.month <= 12
  && d.year >= 1900  
  && d.year <= 2100  
}
predicate IsValidCurrency(currency: string)
{
  exists i :: 0 <= i < |VALID_CURRENCIES| && VALID_CURRENCIES[i] == currency
}
predicate IsValidPesel(pesel: string)
{
  |pesel| == 11 && forall i :: 0 <= i < |pesel| ==> '0' <= pesel[i] <= '9'
}
datatype Option<T> = Some(value: T) | None
class BankSystem {

  var accounts: map<string, Account>

  constructor()
  {
    accounts := map[];
  }
  method CreateAccount(acc: Account) returns (response: bool)
  {
    if acc.pesel in accounts {
      return false;
    }
    accounts := accounts[acc.pesel := acc];
    return true;
  }
  method GetAccountDetails(pesel: string) returns (acc: Option<Account>)
  {
    if pesel in accounts {
      return Some(accounts[pesel]);
    }
    return None;
  }
  method GetCreditCosts(pesel: string, params: CreditParameters) returns (cost: real)
          (params.endDate.year == params.startDate.year && 
           params.endDate.month >= params.startDate.month)
  {

    var account := accounts[pesel];
    var monthDiff := (params.endDate.year - params.startDate.year) * 12 +
                     params.endDate.month - params.startDate.month;


    var interestRate := if account.income > 5000.0 then 0.05 else 0.08;



    var multiplier := 1.0 + interestRate * (monthDiff as real / 12.0);


    cost := params.cost * multiplier;
  }
}

