class Solution {

  method CountConnections(stones: array<Point>) returns (shareList: array<int>)
  {
    shareList := new int[stones.Length];


    var i := 0;
    while i < stones.Length
    {
      shareList[i] := 0;
      i := i + 1;
    }

    i := 0;
    while i < stones.Length
    {
      var j := 0;
      while j < stones.Length
      {
        if i != j {

          if stones[i].x == stones[j].x || stones[i].y == stones[j].y {
            shareList[i] := shareList[i] + 1;
          }
        }
        j := j + 1;
      }
      i := i + 1;
    }
  }

  method RemoveStones(stones: array<Point>) returns (result: int)
  {
    var shareList := CountConnections(stones);


    var isolatedCount := 0;
    var i := 0;
    while i < shareList.Length
    {
      if shareList[i] == 0 {
        isolatedCount := isolatedCount + 1;
      }
      i := i + 1;
    }

    var connectSum := 0;
    i := 0;
    while i < shareList.Length
    {
      connectSum := connectSum + shareList[i];
      i := i + 1;
    }

    if connectSum == 0 {
      result := 0;
    } else {

      result := stones.Length - (isolatedCount + 1);
    }

    if result < 0 {
      result := 0;
    } else if result > stones.Length - 1 {
      result := stones.Length - 1;
    }
  }
}

