datatype NetworkData = NetworkData(userInfo: UserInfo, traceroute: map<int, TracerouteResult>)
datatype Option<T> = Some(value: T) | None
class NetworkProcessor {

    const MAX_HOPS: int := 30

    method TracerouteHop(hopNum: int, targetIp: IpAddress) returns (res: Option<TracerouteResult>)
    {


        var dummyNode := IpAddress([0,0,0,0]);
        res := Some(TracerouteResult(hopNum, dummyNode, 0));
